DELIMITER $$ CREATE FUNCTION quantidade_musicas_no_historico(nome_usuario VARCHAR(50)) RETURNS VARCHAR(50) READS SQL DATA BEGIN DECLARE quantidade INT; SELECT COUNT(c.cancao_id) FROM SpotifyClone.cancao AS c INNER JOIN SpotifyClone.historico AS h ON c.cancao_id = h.cancao_id INNER JOIN SpotifyClone.usuario AS u ON u.usuario_id = h.usuario_id WHERE u.nome_usuario = nome_usuario INTO quantidade; RETURN quantidade; END $$ DELIMITER ; SELECT quantidade_musicas_no_historico('Bill');