SELECT c.nome_cancao AS 'nome', COUNT(h.usuario_id) AS 'reproducoes' FROM SpotifyClone.cancao AS c INNER JOIN SpotifyClone.historico AS h ON c.cancao_id = h.cancao_id INNER JOIN SpotifyClone.usuario AS u ON u.usuario_id = h.usuario_id WHERE u.plano_id <> 1 GROUP BY c.nome_cancao ORDER BY c.nome_cancao;