CREATE VIEW perfil_artistas AS SELECT a.nome_artista AS 'artista', al.nome_album AS 'album', COUNT(s.usuario_id) AS 'seguidores' FROM SpotifyClone.artista AS a INNER JOIN SpotifyClone.seguidor AS s ON a.artista_id = s.artista_id INNER JOIN SpotifyClone.album AS al ON al.artista_id = s.artista_id GROUP BY al.nome_album, a.nome_artista ORDER BY COUNT(s.usuario_id) DESC, a.nome_artista, al.nome_album;